import React, { Component } from 'react';
import Navigation from './components/Navigation/Navigation.js';
import Logo from './components/Logo/Logo.js';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';
import FaceRecognition from './components/FaceRecognition/FaceRecognition';
import Rank from './components/Rank/Rank';
import Particles from 'react-particles-js';
import Clarifai from'clarifai';
import './App.css';

const app = new Clarifai.App({
 apiKey: '59d322a96b5f46a6ae3c325e55bb5090'
});

const particlesOptions={
                particles: {
                   number:{
                    value:100,
                    density:{
                      enable: true,
                      value_area:800,
                      color: {
        value: '#00000'
      },
                    }
                  }
                }
              }

class App extends Component {
constructor(){
  super();
  this.state={
    input:'',
    imageUrl:''
  }

  calculateFaceLocation = (data)=>{
const clarifaiFace = data.outputs[0].data.regions[0].region_info.bounding_box;
const image = document.getElementById('inputimage');
const width = console.log(image.width);
const height = console.log(image.height);
console.log(width,height);

  }
}
onInputChange=(event)=>
{
this.setState({input:event.target.value});
}
onButtonSubmit=()=>{
  this.setState({imageUrl: this.state.input});
  app.models
  .predict(
    Clarifai.FACE_DETECT_MODEL,
    this.state.input).then(response=> this.calculateFaceLocation(response)).catch(err=>console.log(err));

}

  render() {
    return (
      <div className="App">
       <Particles className='particles'
              params={particlesOptions}
              />
      <Navigation />

          <Logo/>
          <Rank/>
          <ImageLinkForm onInputChange={this.onInputChange} onButtonSubmit={this.onButtonSubmit}/>
          <FaceRecognition imageUrl={this.state.imageUrl}/>
      </div>
    );
  }
}
export default App;
